import { useState } from 'react-dom'
import React from 'react'

const Text = ({})=> {
    const[form, SetForm] = useState({}) //setando um state padrão pro nosso formulário
    const [error, setError] = useState({}) // fazendo uma validação bem básica dos campos. 

    function handleChange(e){
        SetForm({
            ...form,
            [e.target.name] :e.target.value
        })
    }

    function handleSubmit(e){
        e.preventDefaut();
    }

    function validate(){
        let errors = {};
        if(!form.name){
            errors.name ='Preencha os campos vázios'
        }

        return errors
    }



    return(
        <>
        <form  onSubmit={e=> handleSubmit(e)}>
            <label htmlFor="name">Nome</label>
            <input 
            name="name" 
            type="text"
            id="name"
            onChange={e=>handleChange(e)}
            />

            <label htmlFor="email">Email</label>
            <input 
            name="email" 
            type="text"
            id="email"
            onChange={e=>handleChange(e)}
            />  
            <label htmlFor="message">Escreva sua mensagem</label>
            <input 
            name="message" 
            type="text"
            id="message"
            max-maxLength='45'
            onChange={e=>handleChange(e)}
            />  
            <button>Enviar</button>
        </form>
        </>
    )
}

export default Text
