const Text = ({ id='modal', onClose = () => {}, Children}) => {

    const handleOutsideClick = (e) => {
        if(e.target.id === id) onClose();
    }


    function onSubmit(values,{ resetForm }){
            console.log(values)
        swal("Formulário preenchido!", "Em breve entraremos em contato!", "success");
        resetForm({ values: '' }) 
        onClose()
    }



    return(       
        <div id={id} className='modalDoar' onClick={handleOutsideClick}>
            <Formik
                    onSubmit={onSubmit}
                    validationSchema={schema2}
                    initialValues={{
                        name: '',
                        date: '',
                        email: ''

                    }}

                    render={({ values, errors, touched, isValid }) => (

                        <Form className='container-form'>

                            <button onClick={(onClose)} className='close'>X</button>

                            <h2 className='title-h2 form-title' >Preencher o formulário</h2>

                            <section className='label'>
                            <label>Nome Completo</label>
                                    <Field
                                    type="text"
                                    name="name"
                                    className='input'
                                    />
                                    <ErrorMessage name='name' />
                            </section>

                            <div className='group1'>
                                <section className='options'>
                                    <label>Data de nasc:</label> 
                                    <Field
                                        type='date'
                                        name='date'
                                        className='input-2'

                                        />
                                    <ErrorMessage name='date' />
                                </section>
                                <section className='options'>
                                <label>Gênero</label>
                                <Field name="genero" component="select" className='input-2' >
                                    <option value="red">Selecione</option>
                                    <option value="red">Masculino</option>
                                    <option value="green">Feminino</option>
                                    <option value="blue">Outros</option>
                                </Field>

                                    {/* <select name="genero"   className='input-2'>
                                        <option  className='input-3' value="selecione">Selecione</option>
                                        <option  className='input-3' value="Masculino">Masculino</option>
                                        <option  className='input-3' value="Fminino">Feminino</option>
                                        <option   className='input-3'value="outros">outros</option>
                                    </select> */}
                                    {/* <ErrorMessage name='genero'/> */}
                                </section>

                            </div>

                            <section className='label section-email'>
                            <label >E-mail</label>
                            <Field
                                type="text"
                                name="email"
                                placeholder="Digite o seu e-mail"
                                className='input'

                            />
                            <ErrorMessage name='email' />
                            </section>

                            <section className='options-2'>
                                <label>Quero ajudar um miau com:</label>
                                <select name="help" id="Genero">
                                    <option value="selecione" >Selecione</option>
                                    <option value="apadrinhar">Apadrinhar</option>
                                    <option value="doar">Fazer doação</option>
                                    <option value="ser amigo">Ser amigo</option>
                                </select>
                            {/* <ErrorMessage name='help'/> */}
                            </section>

                            <button 
                            type='submit' 
                            value='send' 
                            name='send'
                            disabled={!isValid} 
                            className="button-form"          
                            >
                            Enviar                            
                        </button>   

                        </Form>
                    )}

                />
                <div className='content'>{Children}</div>
