import '../Styles/modal/modal.css'
import { Formik, Form, ErrorMessage } from 'formik'
import schema2 from '../Modal/schema2'
import '../Styles/modal/modalDoar.css'
import swal from 'sweetalert';  

import { useState } from 'react'

import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';

// import MenuItem from '@material-ui/core/MenuItem';

// ReactDOM.render(<Demo />, document.querySelector('#root'));

const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
        width: '40ch',
      },
    },
  }));

  const currencies = [
    {
      value: 'Select',
      label: 'Selecione',
    },
    {
      value: 'name',
      label: 'Apadrinhar',
    },
    {
      value: 'name',
      label: 'Fazer doação',
    },
    {
      value: 'name',
      label: 'Ser amigo',
    },
  ];



  const Text = ({ id='modal', onClose = () => {}, Children}) => {

    const handleOutsideClick = (e) => {
        if(e.target.id === id) onClose();
    }

    const classes = useStyles();
    const [currency, setCurrency] = useState('Select');


    function onSubmit(values,{ resetForm }){
        console.log(values)
    // swal("Formulário preenchido!", "Em breve entraremos em contato!", "success");
    resetForm({ values: '' }) 
    swal("Formulário preenchido!", "Em breve entraremos em contato!", "success");
    // onClose()
    }

    const handleChange = (event) => {
        setCurrency(event.target.value);
    };


  return (
    <div id={id} className='modalDoar' onClick={handleOutsideClick}>

    <Formik
    onSubmit={onSubmit}
    validationSchema={schema2}
    initialValues={{
        name: '',
        date: '',
        email: '',
        genero:'',
    }}

    render={({ values, errors, touched, isValid }) => (
        <Form className={classes.root}  autoComplete="on">

         <button onClick={(onClose)} className='close'>X</button>

         <h2 className='title-h2 form-title'>Preencher o formulário</h2>

      <TextField id="standard-basic" type="text" name="name" label="Nome Completo" />
      <ErrorMessage name='name' />

      <TextField id="standard-basic" type='number' name="phone" label="Telefone:" />
      <ErrorMessage name='phone' />

      <TextField id="standard-basic" type='date' name="date" label="" />
      <ErrorMessage name='date' />

      <TextField id="standard-basic" type='name' name="genero" label="Gênero:" />
      <ErrorMessage name='genero' />

      <TextField id="standard-basic" type='email' name="email" label="E-mail:" />
      <ErrorMessage name='email' />
      <h2>Eu quero:</h2>
      <TextField
          id="standard-select-currency-native"
          select
          label=""
          value={currency}
          onChange={handleChange}
          SelectProps={{
            native: true,
          }}
          helperText="Please select your currency"
        >
          {currencies.map((option) => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </TextField>
        <button 
            type='submit' 
            value='send' 
            name='send'
            disabled={!isValid} 
            className="button-form"          
            >
            Enviar                            
        </button>   
    </Form>
    )}
    />
    <div className='content'>{Children}</div>
    </div>
  )
}

export default Text

