import DataHelp from '../Services/DataHelp'
import '../Styles/buttons/button.css'
import '../Styles/components/doar.css'


import { useState } from 'react'
import ModalForm from '../Modal/ModalFormDoar' 


const Doar = () => {

    const[openModal, setOpenModal] = useState(false)


    return(
        <>
            <h2 className='title-h2'>Mas, como posso ajudar?</h2>
            <p className='title-text doar-text'>Se você não pode adotar um gatinho, veja como você pode 
                colaborar para que esses animaiszinhos continuem recebendo
                todo o cuidado, amor e carinho. 
            </p> 
            <div className='card-doar'>
            {
                DataHelp.map(doar =>{
                    return<div key={doar.id} className='card-options'>
                    <section>
                            <img src={doar.image} alt="Foto do gatinho"/>
                            <h3>{doar.title}</h3>
                            <p className='title-text'>{doar.text}</p>
                            {/* <button className='button-secondary button-doar'></button> */}
                            <button onClick={() => setOpenModal(true)} className='button-secondary button-doar'>{doar.button}</button>
                                 {openModal ? <ModalForm onClose={()=> setOpenModal(false)}>
                                              </ModalForm>: null}
                    </section>
                </div>
                })
            }
            </div>

        </>
    )
}

export default Doar
