import { Formik, Field, Form, ErrorMessage } from 'formik'
import schema2 from './schema2'
import '../Styles/modal/modal.css'
import swal from 'sweetalert';  
import Data from '../Services/Data'
import { useState } from 'react'

const ModalForm = ({ id='modal', onClose = () => {}, Children}) => {

    const handleOutsideClick = (e) => {
        if(e.target.id === id) onClose();
    }


    function onSubmit(values,{ resetForm }){
        console.log(values)
    swal("Formulário preenchido!", "Em breve entraremos em contato!", "success");
    resetForm({ values: '' }) 
    onClose()
    }

    const [currency, setCurrency] = useState();

    const handleChange = (event) => {
        setCurrency(event.target.value);
    };

    return(       
        <div id={id} className='modal' onClick={handleOutsideClick}>
            <Formik
                    onSubmit={onSubmit}
                    validationSchema={schema2}
                    initialValues={{
                        name: '',
                        date: '',
                        email: ''
                    }}

                    render={({ values, errors, touched, isValid }) => (

                        <Form className='container-form'>

                            <button onClick={(onClose)} className='close'>X</button>

                            <h2 className='title-h2 form-title' >Preencher o formulário</h2>


                    <section className='label'>
                            <Field type="text" name='name' placeholder="Nome Completo:" className='input'/>
                                <ErrorMessage name='name' />

                             <Field type="phonenumber" name='phone' placeholder="Telefone: ()" className='input'/>
                                <ErrorMessage name='phone' />

                            <Field type="date" name='date' className='input'/>
                                 <ErrorMessage name='date' />

                            <Field type="text" name='genero' placeholder="Genero:" className='input'/>
                                 <ErrorMessage name='genero' />

                            <Field type="text" name='email' placeholder="E-mail:" className='input'/>
                                 <ErrorMessage name='email' />
                         </section>

                        <div className='group1'>
                            <p className='text'> Qual gatinho você vai adotar?</p>
                             <Field
                                value={currency}
                                onChange={handleChange}
                                SelectProps={{
                                    native: true,
                                }}
                               >
                                 {Data.map((option) => (
                                    <option key={option.id} value={option.id}>
                                            {option.name}
                                    </option>
                                ))}
                            </Field>
                        </div>




                            <section className='options-2'>
                                <label>Quero ajudar um miau com:</label>
                                <select name="help" id="Genero">
                                    <option value="selecione" >Selecione</option>
                                    <option value="apadrinhar">Apadrinhar</option>
                                    <option value="doar">Fazer doação</option>
                                    <option value="ser amigo">Ser amigo</option>
                                </select>
                            {/* <ErrorMessage name='help'/> */}
                            </section>

                            <button 
                            type='submit' 
                            value='send' 
                            name='send'
                            disabled={!isValid} 
                            className="button-form"          
                            >
                            Enviar                            
                        </button>   
                        </Form>
                    )}
                />
                <div className='content'>{Children}</div>
        </div>
    )
}

export default ModalForm
