import '../Styles/modal/modal.css'
// import { Formik, Field, Form, ErrorMessage } from 'formik'
// import swal from 'sweetalert';
// import schema2 from './schema2'
import Modal from 'react-modal'


const ModalForm = ({id='modal', onClose = () => {}}) => {

    const handleOutsideClick = (e) => {
        if(e.target.id == id){
            onClose()
        }
    }

    function onSubmit(values, { resetForm }){
        console.log(values)
        resetForm({ values: '' })
        swal("Mensagem enviada!", "Em breve entraremos em contato!", "success");
    }

    return (
        <div className="big-modal" onClick={handleOutsideClick}>
            <Modal isOpen={modalIsOpen} className="content-modal">
                <Formik
                    onSubmit={onSubmit}
                    validationSchema={schema2}
                    initialValues={{
                        name: '',
                        // genero: '',
                        date: '',
                        email: '',
                        // help:''
                    }}

                    render={({ values, errors, touched, isValid }) => (
                        <Form className='form'>
                            <h2>Preencher o formul√°rio</h2>
                            <div className='group1'>
                                <section className='name'>
                                    <label>Nome Completo</label>
                                    <Field
                                    type="text"
                                    name="name"
                                    placeholder="Digite o seu nome"
                                    className='input'
                                    />
                                    <ErrorMessage name='name' />
                                </section>
                                <section className='select-genero'>
                                    <label>Selecione um G√™nero</label>
                                    <select name="genero" id="Genero">
                                        <option value="selecione">Selecione</option>
                                        <option value="Masculino">Masculino</option>
                                        <option value="Fminino">Feminino</option>
                                        <option value="outros">outros</option>
                                    </select>
                                    {/* <ErrorMessage name='genero'/> */}
                                </section>
                            </div>
                            <Field
                            type='date'
                            name='date'
                            />
                            <ErrorMessage name='date' />

                            <label>E-mail</label>
                            <Field
                                type="text"
                                name="email"
                                placeholder="Digite o seu e-mail"
                                className='input'
                            />
                            <ErrorMessage name='email' />

                            <label>Como voc√™ pode ajudar?</label>
                            <select name="help" id="Genero">
                                <option value="selecione" >Selecione</option>
                                <option value="apadrinhar">Apadrinhar</option>
                                <option value="doar">Fazer doa√ß√£o</option>
                                <option value="ser amigo">Ser amigo</option>
                            </select>
                            {/* <ErrorMessage name='help'/> */}

                            <button 
                            type='submit' 
                            value='send' 
                            disabled={!isValid} 
                            className="button-form"
                            onClick={onClose}
                            >
                            Enviar                            
                        </button>  

                        </Form>
                    )}

                />

            </Modal>
        </div>
    )
}

export default ModalForm
