import * as Yup from 'yup'

export default Yup.object().shape({
    name: Yup.string().min(2).required("obrigatótio"),
    email: Yup.string().email().required("obrigatótio"),
    date: Yup.date().required("obrigatótio").nullable(),
    phone: Yup.number().required('obrigatório').nullable(),
    genero: Yup.string().required('obrigatório'),
    // acceptTerms: Yup.object().shape({acceptTerms: Yup.bool().oneOf([true]).required('Aceite os termos e consições para continuar')}).nullable('Aceite os termos e consições para continuar')
    acceptTerms:Yup.object().shape({
        acceptTerms: Yup.bool().oneOf([true], 'Aceite os termos e condições para continuar')
    })
})
