import Data from '../Services/Data'
import { useState } from 'react'

import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import swal from 'sweetalert';  

const Text = ({ id = 'modal', onClose = () => { }, Children }) => {


    const handleOutsideClick = (e) => {
        if (e.target.id === id) onClose();
    }

    const useStyles = makeStyles((theme) => ({
        root: {
            '& > *': {
                margin: theme.spacing(1),
                width: '40ch',
            },
        },
    }));



    const classes = useStyles();
    const [currency, setCurrency] = useState('');

    const handleChange = (event) => {
        setCurrency(event.target.value);
    };


    function onSubmit(value){
        console.log(value)
    swal("Formulário preenchido!", "Em breve entraremos em contato!", "success");
    // resetForm({ values: '' }) 
    onClose()
    }


    return (
        <div id={id} className='modalDoar' onClick={handleOutsideClick}>

            <form className={classes.root} Validate autoComplete="on">

                <button onClick={(onClose)} className='close'>X</button>

                <h2 className='title-h2 form-title' >Preencher o formulário</h2>

                <TextField id="standard-basic" label="Nome Completo" />
                <TextField id="standard-basic" type='number' label="Telefone:" />
                <TextField id="standard-basic" type='date' label="" />
                <TextField id="standard-basic" type='name' label="Gênero:" />
                <TextField id="standard-basic" type='email' label="E-mail:" />
                <h2>Eu quero adotar:</h2>
                <TextField
                    id="standard-select-currency-native"
                    select
                    label="Selecione o nome:"
                    value={currency}
                    onChange={handleChange}
                    SelectProps={{
                        native: true,
                    }}
                >
                    {Data.map((option) => (
                        <option key={option.id} value={option.id}>
                            {option.name}
                        </option>
                    ))}
                </TextField>

                <button
                    type='submit'
                    value='send'
                    name='send'
                    // disabled={!isValid} 
                    className="button-form"
                    onSubmit={onSubmit}
                >
                    Enviar
                </button>
            </form>

            <div className='content'>{Children}</div>
        </div>
    )

}

export default Text
