import { useState } from 'react-dom'

function App() {

  const[values, setValues] = useState([
    {
      name:'',
      // email:'',
      // message:''
    }
  ])

  function onChange(e){
    setValues({
      ...values,
      [e.target.name] : e.target.value
    })
  }

  return (
    <div className="App">
        <h1>Contato</h1>
        <form>
          <input
