import { useState } from 'react'
import { Formik } from 'formik'

const App = () => {
  const [values, setValues] = useState({
    name: ''
  })

  function onChange(e){
    setValues({
      ...values,
      [e.target.name] : e.target.value,

    })
  }

  return (
    <div>
      <h1>Contato</h1>
        <form>
          <input
              onChange={onChange}
              value={values.name}
              type="text"
              name="name"
              placeholder="Digite o seu nome"
          />
          <input
              onChange={onChange}
              value={values.email}
              type="text"
              name="email"
              placeholder="Digite o seu e-mail"

          />
          <input
              onChange={onChange}
              value={values.message}
              type="text"
              name="message"
              placeholder="Mensagem"
          />
          <button type="submit"></button>
        </form>

    </div>
  )
}

export default App
