import './projeto/Styles/global.css'
// import Route from './projeto/Routes/Routes'

import { useState } from 'react-dom'

function App() {

  const[values, setValues] = useState([
    {
      name:'',
      // email:'',
      // message:''
    }
  ])

  function onChange(e){
    setValues({
      ...values,
      [e.target.name] : e.target.value
    })
  }


  return (
    <div className="App">
      <div>
        <h1>Contato</h1>
        <form>
          <input
            onChange={onChange}
            value={values.name}
            type="text"
            name="name"
            placeholder="Digite o seu nome"
          />
          <input
            onChange={onChange}
            value={values.email}
            type="text"
            name="email"
            placeholder="Digite o seu e-mail"
          />
          <input
            onChange={onChange}
            value={values.message}
            type="text"
            name="message"
            placeholder="Mensagem"
          />
          <button type="submit"></button>
        </form>
      </div>
  )
}

