import Data from '../Services/Data'
import { useState } from 'react'
import schema2 from '../Modal/schema2'
import { Formik,Form, ErrorMessage } from 'formik'
import '../Styles/modal/modalDoar.css'

import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import swal from 'sweetalert';  


const Text = ({ id = 'modal', onClose = () => { }, Children }) => {

    const handleOutsideClick = (e) => {
        if (e.target.id === id) onClose();
    }

    const useStyles = makeStyles((theme) => ({
        root: {
            '& > *': {
                margin: theme.spacing(1),
                width: '40ch',
            },
        },
    }));



    const classes = useStyles();
    const [currency, setCurrency] = useState();

    const handleChange = (event) => {
        setCurrency(event.target.value);
    };


    function onSubmit(values, {resetForm}){
        console.log(values)
        swal("Formulário preenchido!", "Em breve entraremos em contato!", "success");
        resetForm({ values: '' }) 
        onClose()
    }

    return (
        <div id={id} className='modalDoar' onClick={handleOutsideClick}>

            <Form  
            onSubmit={onSubmit}
            validationSchema={schema2}
            id='container-form'
            className={classes.root}
            autoComplete="on" 
             >
                <button onClick={(onClose)} className='close'>X</button>

                <h2 className='title-h2 form-title' >Preencher o formulário</h2>

                <TextField id="standard-basic" name='name' label="Nome Completo" />
                <ErrorMessage name='name' />

                <TextField id="standard-basic" type='number' name='phone' label="Telefone:" />
                <ErrorMessage name='phone' />

                <TextField id="standard-basic" type='date'  name='date' label="" />
                <ErrorMessage name='date' />

                <TextField id="standard-basic" type='name' name='genero' label="Gênero:" />
                <ErrorMessage name='genero' />

                <TextField id="standard-basic" type='email' name='email' label="E-mail:" />
                <ErrorMessage name='email' />

                <h2>Eu quero adotar:</h2>
                <TextField
                    id="standard-select-currency-native"
                    select
                    label="Selecione o nome:"
                    value={currency}
                    onChange={handleChange}
                    SelectProps={{
                        native: true,
                    }}
                >
                    {Data.map((option) => (
                        <option key={option.id} value={option.id}>
                            {option.name}
                        </option>
                    ))}
                </TextField>

                <button
                    type='submit'
                    value='send'
                    name='send'
                    disabled={!isValid} 
                    className="button-form"   
                >
                    Enviar
                </button>
            </Form>
            )}
            <div className='content'>{Children}</div>
        </div>
    )

}

export default Text
